sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onStart*/ ) {

    // Add your code here
    var conso_model = new GlideRecord('x_keral_lab_consumable_product_model');
    conso_model.addQuery('name', source.u_model_id);
    conso_model.query();
    if (conso_model.next()) return;


    conso_model.setValue('name', source.u_model_id);
    conso_model.setValue('default_unit', source.u_unit);
	conso_model.setValue('risk_family', source.u_risk);
	conso_model.setValue('cas_number', source.u_cas_number);
	conso_model.setValue('manufacturer', source.u_manufacturer);
	conso_model.setValue('owner', source.u_owner);
	conso_model.setValue('minimum_stock', source.u_quantity_minimum);


    conso_model.insert();

})(source, map, log, target);
